mapper.select:
SELECT * FROM cart_items
WHERE user_id = #{user_id}
ORDER BY shop_id, added_time DESC

mapper.update:
UPDATE cart_items
SET quantity = #{quantity}
WHERE user_id = #{user_id} AND product_id = #{product_id}

UPDATE cart_items
SET quantity = #{quantity}
WHERE cart_item_id = #{cart_item_id}
