mapper.update:
UPDATE orders
SET status = #{status},
    payment_method = #{payment_method},
    pay_time = #{pay_time}
WHERE order_id = #{order_id}

UPDATE orders
SET status = "delivered"
WHERE order_id = #{order_id}

UPDATE orders
SET status = #{status},
    completed_time = #{completed_time}
WHERE order_id = #{order_id}
